.corx {
    font-weight: 900;
    background: -webkit-linear-gradient(20deg,#F4B623,#CF7922);
     -webkit-background-clip: text;
-webkit-text-fill-color: transparent;
animation: 3s cubic-bezier(0.2, 0.5, 0.9, 0.6) 2s infinite normal none running textodegradex2;
    background-image: linear-gradient(45deg, #F4B623, #CF7922, #F4B623, #CF7922);
    background-size: 400% 200%;
    transition: all .8s;
}
@keyframes textodegradex2{
    0% {
        background-position: 0 0;
    }
    100% {
        background-position: 100% 0;
    }
}

